	function send_mail($too,$subject,$message)
    {
        $flag = false;

        $to = $too;
        $sub = $subject;
        $msg = '<html><head><style>body {  background-color: silver;  }</style></head><body><table><tr><td><img height="100" width="300" src="data:image/png;base64,"/></td><td><h1>'.$subject.'</h1><p>'.$message.'</p></td></tr></table></body></html>';

        /*important lines for html mails*/
        $header = "MIME-Version:1.0"."\r\n";
        $header = $header."Content-type:text/html;charset=utf-8"."\r\n";
        /*important lines for html mails*/

        $header = $header."From:lighthousebangladesh <lighthousebangladesh.com>"."\r\n";
        $header = $header.'Cc:  info@lighthousebangladesh.com' . "\r\n";
        $header = $header.'Bcc:  info@lighthousebangladesh.com' . "\r\n";

        if(mail($to,$sub,$msg,$header))
        {
            $flag = true;
        }

        return $flag;
    }

