                    <script type="text/javascript">
                        //send message
                        function zn()
                        {
                            data = {};
                            data['name'] = $("#name").val();
                            data['email'] = $("#email").val();
                            data['msg'] = $("#msg").val();

                            if(($.trim(data['name']) != '') && ($.trim(data['email']) != '') && ($.trim(data['msg']) != ''))
                            {
                                if(data['email'].indexOf('@') != '-1')
                                {
                                    http = '<? echo base_url().'site/send_msg' ?>';
                                    $.ajax({
                                        url:http,
                                        method:'POST',
                                        data:{'packet':JSON.stringify(data)},
                                        success:function(data)
                                        {
                                            if(data == '0')
                                            {
                                                $("#contact_form").hide();
                                                $("#result").html('<div class="alert alert-success" role="alert">Message Successfully Sent</div>');
                                            }
                                        }
                                    });
                                }
                                else
                                {
                                    $("#min_email").html('<p style=color:red;>PLEASE GIVE A VALID EMAIL ADDRESS.</p>');
                                    $("input[name=email]").css("border", "1px solid red");
                                    $("#min_res").html('');
                                    $("input[name=name]").css("border", "");
                                    $("textarea[name=msg]").css("border", "");
                                }
                            }
                            else
                            {
                                $("#min_res").html('<div class="alert alert-danger" role="alert">PLEASE FILL UP ALL THE FLIEDS.</div>');
                                $("input[name=email]").css("border", "1px solid red");
                                $("input[name=name]").css("border", "1px solid red");
                                $("textarea[name=msg]").css("border", "1px solid red");

                            }
                        }


                        $("#msg").on('keyup',function(e) {
                            if(e.which == 13)
                            {
                                zn();
                            }
                        });

                        $("#submit_btn").click(

                            function()
                            {
                                zn();
